import { useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector, useDispatch } from "react-redux";
import { cartAction } from "./store";
import Notification from "./components/UI/Notification";
let isInitial = true;
function App() {
  const show = useSelector((state) => state.cart.showCart);
  const cart = useSelector((state) => state.cart.cartItems);
  const showNotification = useSelector((state) => state.cart.notification);
  const dispatch = useDispatch();
  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        cartAction.showNotification({
          status: "pending",
          title: "sending...",
          message: "sending cart data",
        })
      );
      const response = await fetch(
        "https://react-redux-eb861-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("sending cart data failed");
      }

      dispatch(
        cartAction.showNotification({
          status: "success",
          title: "Success!",
          message: "sending cart data Success",
        })
      );
    };
    if (isInitial) {
      isInitial = false;
      return;
    }
    sendCartData().catch((error) => {
      dispatch(
        cartAction.showNotification({
          status: "error",
          title: "Error!",
          message: "sending cart data failed",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <>
      {showNotification && (
        <Notification
          status={showNotification.status}
          title={showNotification.title}
          message={showNotification.message}
        />
      )}
      <Layout>
        {show && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
